import React  from "react";
import axios from 'axios';
import Table from 'react-bootstrap/Table';


const Pizza = props =>{
    const [data, setData] = React.useState([]);
    let datarows = React.useState([]);
    let datacols = React.useState([]);
    let columns = []
    let rows = []
    let TestTable

    React.useEffect(()=>{
        axios('http://localhost:5000/pizza')
        .then(response=>{
            if(response.status===200){
                const datarows =response.data.rows;
                console.log('----------------------s')
                for (let i = 0; i < datacols.length; i++){
                  datacols = response.data.metaData[i].name;
                  columns.push(datacols)
                  console.log(columns)
                }
                for(var i = 0; i < datarows.length; i++){
                    rows = rows.concat(datarows[i]);
                }                
                console.log(rows)
                console.log(columns)
                console.log(datacols)
                console.log(datarows);
                setData(datarows);
            }
        })
        .catch(err=>{
            console.log(err);
        })
    },[]);

    let datadisplay=<div id='datadisplay'>----</div>;

    const test = () => {
        console.log('TEST')
        axios('http://localhost:5000/pizza')
            .then(response=>{
                if(response.status===200){
                    datadisplay=<div id='datadisplay'>test</div>
                    datarows =response.data.rows;
                    console.log(datarows);
                    setData(datarows);
                    console.log(datarows[1]);
                    console.log(columns)
                    //return (datadisplay)
                }
            })
            .catch(err=>{
                console.log(err);
            })
    }

    return(<div>
        Pizzas
        <div>----------------------------</div>
        {
            data.length>0 &&
            data.map((item,index)=>
            <div>
            <div key={index}>{index} - {' ' + item + ' '}</div>
            <button id='button2' onClick={test}>Add</button>
            <div>----------------------------</div>
            </div>
            )
        }

        <button id='button' onClick={test}>Pizzas</button>
        {datadisplay}

    </div>)
}

export default Pizza;
